css:
@media print {
     @page {
        margin-top: 4cm;  /* Adjust this value for the top margin */
        margin-bottom: 4cm; /* Adjust this value for the bottom margin */
    }

    body {
        margin: 0; /* Ensure the body has no extra margin */
    }
}


.color_black {
    color: black !important;
}
.footer{
    position:relative;
    margin-top: 20px;
}
.seller_table {
    width: 100%;
    table-layout: fixed;
}

.seller_table, .seller_table th, .seller_table td {
    border: 1.8px solid black;
    border-collapse: collapse;
    font-size: 10px;
    line-height: 0.8;
    color: black !important;
}

.bold {
    font-weight:bold;
}
.inc_size {
    font-weight:550;
}

.seller_table thead {
    background-color: #cccccc;
}

.color_bg_gray {
    background-color: #cccccc;
}

.print-format td,.print-format th {
    padding: 5px 3px !important;
    font-family: 'Times New Roman', Times, serif;
}

body {
    transform: scale(1.0);
}

