{% set company = frappe.get_doc("Company", doc.company)%}

{% if (doc.company_address) %}
    {% set supplier_address_doc = frappe.get_doc('Address', doc.company_address) %}
{% endif %}


{% if(doc.customer) %}
    {% set customer = frappe.get_doc('Customer', doc.customer ) %}
{% endif %}

{% if(doc.customer_address) %}
    {% set customer_address = frappe.get_doc('Address', doc.customer_address ) %}
{% endif %}

{% if(doc.shipping_address_name) %}
    {% set customer_shipping_address = frappe.get_doc('Address', doc.shipping_address_name ) %}
{% endif %}
{% if(doc.dispatch_address_name) %}
    {% set customer_dispatch_address = frappe.get_doc('Address', doc.dispatch_address_name ) %}
{% endif %}


<!--{% if letter_head and not no_letterhead -%}-->
<!--    {% if print_settings.repeat_header_footer %}-->
<!--            <div class="letter-head">-->
<!--                {{ letter_head }}-->
<!--            </div>-->
<!--    {%- endif %}-->
<!--{%- endif %}-->

<div class="row">
    <div class="col-md-12 col-sm-12">
        <table class="seller_table">
            <colgroup>
                <col style="width: 20%;">
                <col style="width: 60%;">
                <col style="width: 20%;">
            </colgroup>
            <tbody>
                <tr> 
                    <td colspan="3" class="bold text-center" style="font-size: 15px;">Tax Invoice / فاتورة ضريبية</td>
                </tr>
                <tr>
                    <td class="bold" style="font-size: 13px;">Invoice No:</td>
                    <td class="text-center" style="font-size: 13px;">{{ doc.name }}</td>
                    <td dir="rtl" class="inc_size" style="font-size: 13px;">رقم الفاتورة</td>
                </tr>
                <tr>
                    <td class="bold" style="font-size: 13px;">Date Of Invoice:</td>
                    <td class="text-center" style="font-size: 13px;">{{ doc.posting_date }}</td>
                    <td dir="rtl" class=" inc_size" style="font-size: 13px;">تاريخ الفاتورة</td>
                </tr>
                <tr>
                    <td colspan="3" style="height: 25px;"></td>
                </tr>
                
                <tr>
                    <td colspan="3" class="text-center bold" style="font-size: 15px;">Customer Details/تفاصيل العميل</td>
                </tr>
                <tr>
                    <td class="bold" style="font-size: 13px;">Client Name:</td>
                    <td class="text-center" style="font-size: 13px;">{{customer.customer_name or""}} <br> {{customer.customer_name_in_arabic or""}}</td>
                    <td class="text-right inc_size" style="font-size: 13px;">اسم العميل</td>
                </tr>
                <tr>
                    <td class="bold" style="font-size: 13px;">Client VAT #:</td>
                    <td class="text-center" style="font-size: 13px;">{{customer.tax_id or""}}</td>
                    <td class="text-right inc_size" style="font-size: 13px;">رقم الضريبة</td>
                </tr>
               <tr>
                    <td class="bold" style="font-size: 13px;">Client Address:</td>
                    <td class="text-center" style="font-size: 13px;">
                        {% if(customer_address) %}
                            <span>{{ customer_address.address_line1}} </span> <br>
                            {{ customer_address.address_in_arabic or ""}}
                        {% endif %}
                    </td>
                    <td class="text-right inc_size" style="font-size: 13px;">عنوان العميل</td>
                </tr>
                <tr>
                    <td class="bold" style="font-size: 13px;">Attn:</td>
                    <td class="text-center" style="font-size: 13px;">{{ doc.attn or""}}</td>
                    <td class="text-right inc_size" style="font-size: 13px;">ملاحظة</td>
                </tr>
                <tr>
                    <td class="bold" style="font-size: 13px;">Delivered To:</td>
                    <td class="text-center" style="font-size: 13px;">
                        {% if(doc.delivered_to) %}
                            {{ doc.delivered_to or "" }}
                        {% else %}
                            {% if(customer_shipping_address) %}
                                <span>{{ customer_shipping_address.address_line1}} </span> <br>
                                {{ customer_shipping_address.address_in_arabic or ""}}
                            {% endif %}
                        {% endif %}
                    </td>
                    <td class="text-right inc_size" style="font-size: 13px;">سلمت ل</td>
                </tr>
                <tr>
                    <td class="bold" style="font-size: 13px;">P.O. Number:</td>
                    <td class="text-center" style="font-size: 13px;">{{ doc.po_no or"" }}</td>
                    <td class="text-right inc_size" style="font-size: 13px;">رقم طلب الشراء</td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
{% set total = namespace(amount = 0) %}
<div class="row">
    <div class="col-md-12 col-sm-12">
        <table class="seller_table" style="width: 100%;">
            <thead>
                <tr>
                    <th class="text-center color_black" style="width:7%;">
                        <b> SL. NO. <br> مسلسل</b>
                    </th>
                    <th class="text-center color_black" style="width:30%;">
                        <b> Service Description <br> وصف الخدمة</b>
                    </th>
                    <th class="text-center color_black">
                        <b> Qty <br> كمية</b>
                    </th>
                    <th class="text-center color_black">
                        <b> Unit Rate <br> سعر الوحدة</b>
                    </th>
                    <th class="text-center color_black">
                        <b> Price Excl. Vat <br> سعر ضريبة القيمة المضافة الحصري </b>
                    </th>
                    <th class="text-center color_black">
                        <b> VAT <br> الضريبة</b>
                    </th>
                    <th class="text-center color_black">
                        <b> VAT AMT <br> المبلغ الضريبي</b>
                    </th>
                    <th class="text-center color_black">
                        <b> Amount Inclusive Vat <br> المجموع مع الضريبة</b>
                    </th>
                </tr>
            </thead>
            <tbody>
                {%- for item in doc.items -%}
                {% set total.amount = item.amount %}
                <tr class="text-center">
                    <td style="font-size: 12px !important;">{{ item.idx }}</td>
                    <td style="font-size: 12px !important;">
                        {{ item.item_name }}
                        {%- if item.item_code != item.item_name -%}
                        <br>Item Code: {{ item.item_code}}
                        {%- endif -%}
                    </td>
                    <td style="font-size: 12px !important;">{{ item.qty }}</td>
                    <td style="font-size: 12px !important;">{{ "{:,.2f}".format(item.rate) }}</td>
                    <td style="font-size: 12px !important;">{{ "{:,.2f}".format(item.amount) }}</td>
                    <td style="font-size: 12px !important;">
                        {%- if(doc.taxes) -%}
                            {% for row in doc.taxes %}
                                {% set data_object = json.loads(row.item_wise_tax_detail) %}
                                {% set key = item.item_code or item.item_name %}
                                 {% set tax_amount = (item.amount * row.rate ) / 100 %}
                                {%- if(data_object[key][0])-%}
                                    {{ frappe.format(data_object[key][0], {'fieldtype': 'Percent'}) }}
                                 {% else %}
                                 {{tax_amount }}
                                {%- endif -%}
                            {% endfor %}
                        {% else %}
                            0%
                        {%- endif -%}
                    </td>
                    <td style="font-size: 12px !important;">
                        {%- if(doc.taxes) -%}
                            {% for row in doc.taxes %}
                                {% set data_object = json.loads(row.item_wise_tax_detail) %}
                                {% set key = item.item_code or item.item_name %}
                                {% set tax_amount = (item.amount * row.rate ) / 100 %}
                               
                                {%- if(data_object[key][1])-%}
                                    <span>{{ "{:,.2f}".format(tax_amount) }}</span>
                                {% else %}
                                 <span>{{tax_amount }}</span>
                                    {% set total.amount = total.amount + tax_amount %}
                                {%- endif -%}
                            {% endfor %}
                        {% else %}
                            0.00
                        {%- endif -%}

                    </td>
                    <td style="font-size: 12px !important;"> {{ "{:,.2f}".format(total.amount) }}</td>
                </tr>
                {%- endfor -%}
            </tbody>
        </table>
    </div>
</div>

<div class="row">
    <div class="col-md-12 col-sm-12">
        <table class="seller_table mb-2">
            <tr>
                <th colspan="4" class="color_bg_gray bold" style="font-weight: bold;font-size: 15px;">
                    <span>Total Amounts</span>
                    <span style="float: right;">إجمالي المبالغ</span>
                </th>
            </tr>
            <tr>
               <td class="color_black inc_size bold" style="font-size: 13px;">Sub Total</td>
