                <td colspan="2" class="inc_size text-right bold" style="font-size: 13px;">{{ doc.currency }} المجموع</td>
                <td class="inc_size text-right bold" style="font-size: 13px;">{{ "{:,.2f}".format(doc.grand_total) }}</td>
            </tr>
            {% if(doc.currency != 'SAR') %}
                <tr>
                    <td class="color_black inc_size bold text-right" style="font-size: 13px;">القيمة الخاضعة للضريبة بالريال السعودي</td>
                    <td colspan="2" class="inc_size text-right bold" style="font-size: 13px;"> ريال السعودي</td>
                    <td class="inc_size text-right bold" style="font-size: 13px;">{{ "{:,.2f}".format(doc.base_total) }}</td>
                </tr>
                <tr>
                    <td dir="rtl" class="color_black inc_size bold" style="font-size: 13px;">القيمة قيمة الضريبة المضافة بالريال15 %</td>
                    <td colspan="2" class="inc_size text-right bold" style="font-size: 13px;">ريال السعودي</td>
                    <td class="inc_size text-right bold" style="font-size: 13px;"> {{ "{:,.2f}".format(doc.base_total_taxes_and_charges) }}</td>
                </tr>
            {% endif %}
            <tr>
                <td colspan="4" class="color_black inc_size bold" style="font-size: 13px;">Total: {{ doc.in_words }}<br>{{ doc.in_worlds_arabic or"" }}</td>
            </tr>
            {% if(doc.bank_account) %}
                {% set bank_account = frappe.get_doc('Bank Account', doc.bank_account ) %}
                {% set bank = frappe.get_doc('Bank', bank_account.bank ) %}
            {% endif %}
            <tr>
                <td colspan="2">
                    <span>{{doc.terms or""}}</span> <br>
                    <span>{{doc.bank_details or""}}</span>
                </td>
                <td class="text-center">
                    {% if(doc.status != 'Draft') %}
                         {% set ur = frappe.get_list('File', filters={ 'Attached_to_name': doc.name}, fields=['file_url']) %}
                    {% for u in ur %}
                    {% if u.file_url.endswith('.png') %}
                    <img  src="{{u.file_url}}">
                    {% else %}
                    <span></span>
                    {% endif %}
                    {% endfor %} 
                    {% endif %}
                </td>
                <td></td>
            </tr>
        </table>
    </div>
