html:
{% set company = frappe.get_doc("Company", doc.company)%}
{% if (doc.company_address) %}
    {% set supplier_address_doc = frappe.get_doc('Address', doc.company_address) %}
{% endif %}


{% if(doc.customer) %}
    {% set customer = frappe.get_doc('Customer', doc.customer ) %}
{% endif %}

{% if(doc.customer_address) %}
    {% set customer_address = frappe.get_doc('Address', doc.customer_address ) %}
{% endif %}

{% if letter_head and not no_letterhead -%}
    {% if print_settings.repeat_header_footer %}
        <div id="header-html" class="hidden-pdf">
            <div class="letter-head">
                {{ letter_head }}
            </div>
        </div>
    {%- endif %}
{%- endif %}

<div>
    <p class="bold text-center" style="font-size: 22px;margin-top:15px;">
        <span>Tax Invoice</span> <br>
        <span>فاتورة ضریبیة</span>
    </p>
        
    <div class="row">
        <div class="col-md-12 col-sm-12">
            <table class="seller_table">
                <thead>
                    <tr>
                        <th class="color_black text-center" colspan="3"><span class="bold" style="font-size: 14px;">Buyer: بيانات العميل</span></th>
                    </tr>
                </thead>
                <tbody>
                    <tr class="text-center">
                        <td style="font-size:13px;" colspan="3">{{customer.customer_name or""}} <br><br>{{customer.customer_name_in_arabic or""}}</td>
                    </tr>
                    <tr class="text-center">
                        <td style="font-size:13px;">Customer Address</td>
                        <td style="font-size:12px;">
                            {% if(customer_address) %} 
                                <span>{{ customer_address.address_line1 or""}} </span> <br>
                                {{ customer_address.address_in_arabic or""}}
                            {% endif %}
                        </td>
                        <td style="font-size:12px;">عنوان العميل</td>
                    </tr>
                    {% if(customer.tax_id) %}
                        <tr class="text-center">
                            <td style="font-size:13px;">VAT Number</td>
                            <td style="font-size:13px;">{{customer.tax_id or""}}</td>
                            <td style="font-size:12px;">رقم ضريبة القيمة المضافة</td>
                        </tr>
                    {% endif %}
                    {% if(doc.project) %}
                        <tr class="text-center">
                            <td style="font-size:13px;">Project Name</td>
                            <td style="font-size:13px;">{{ doc.project or"" }}</td>
                            <td style="font-size:12px;">اسم المشروع</td>
                        </tr>
                    {% endif %}
                    {% if(doc.contract) %}
                        <tr class="text-center">
                            <td style="font-size:13px;">Contract</td>
                            <td style="font-size:13px;">{{ doc.contract or"" }}</td>
                            <td style="font-size:12px;">عقد</td>
                        </tr>                    
                    {% endif %}
                </tbody>
            </table>
        </div>
    </div>
        
    <div class="row">
        <div class="col-md-12 col-sm-12">
            <table class="seller_table">
                <thead>
                    <tr>
                        <th class="color_black text-center" colspan="3"><span class="bold" style="font-size: 14px;">Seller: اسم البائع</span></th>
                    </tr>
                </thead>
                <tbody>
                    <tr class="text-center">
                        <td style="font-size:13px;" colspan="3">{{ company.name or"" }} <br><br>{{ company.company_name_in_arabic or"" }}</td>
                    </tr>
                    <tr class="text-center">
                        <td style="font-size:13px;">Seller Address</td>
                        <td style="font-size:12px;">
                            {% if(supplier_address_doc) %} 
                                <span>{{ supplier_address_doc.address_line1 }} </span> <br>
                                {{ supplier_address_doc.address_in_arabic or ""}}
                            {% endif %}
                        </td>
                        <td style="font-size:12px;">عنوان البائع</td>
                    </tr>
                    {% if(company.tax_id) %}
                        <tr class="text-center">
                            <td style="font-size:13px;">VAT Number</td>
                            <td style="font-size:13px;">{{company.tax_id or""}}</td>
                            <td style="font-size:12px;">رقم ضريبة القيمة المضافة</td>
                        </tr>
                    {% endif %}
                    {% if(doc.po_no) %}
                        <tr class="text-center">
                            <td style="font-size:13px;">Purchase Order No</td>
                            <td style="font-size:13px;">{{ doc.po_no or"" }}</td>
                            <td style="font-size:12px;">رقم أمر الشراء  </td>
                        </tr>
                    {% endif %}
                    {% if(doc.po_date) %}
                        <tr class="text-center">
                            <td style="font-size:13px;">Purchase Order Date</td>
                            <td style="font-size:13px;">{{ doc.po_date or"" }}</td>
                            <td style="font-size:12px;">تاريخ أمر الشراء</td>
                        </tr>
                    {% endif %}          
                    <tr class="text-center">
                        <td style="font-size:13px;">Invoice No</td>
                        <td style="font-size:13px;">{{ doc.name or"" }}</td>
                        <td style="font-size:12px;">رقم الفاتورة</td>
                    </tr>
                    <tr class="text-center">
                        <td style="font-size:13px;">Invoice Date</td>
                        <td style="font-size:13px;">{{ doc.posting_date or"" }}</td>
                        <td style="font-size:12px;">تاريخ الفاتورة</td>
                    </tr>
                    <tr class="text-center">
                        <td style="font-size:13px;">Payment Due Date</td>
                        <td style="font-size:13px;">{{ doc.due_date }}</td>
                        <td style="font-size:12px;">التاريخ المحدد للدفع</td>
                    </tr>                    
                </tbody>
            </table>
        </div>
    </div>
        
    <div class="row table_div">
        <div class="col-md-12 col-sm-12">
            <table class="seller_table item_tables">
                <thead>
                    <tr>
                        <th class="text-center color_black" style="font-size: 12px;width: 35%;"><b>Nature of Goods/ Services<br>تفاصيل السلع أو الخدمات</b></th>
                        <th class="text-center color_black" style="font-size: 12px;width: 10%;"><b>Qty<br>الكمية</b></th>
                        <th class="text-center color_black" style="font-size: 12px;width: 10%;"><b>Unit Price<br>سعر الوحده</b></th>
                        <th class="text-center color_black" style="font-size: 12px;width: 15%;"><b>Taxable Amount<br>المبلغ الخاضع للضريبة</b></th>
                        <th class="text-center color_black" style="font-size: 12px;width: 10%;"><b>VAT 15%<br>الضريبة</b></th>
                        <th class="text-center color_black" style="font-size: 12px;width: 10%;"><b>VAT Amount<br>قيمة الضريبة</b></th>
                        <th class="text-center color_black" style="font-size: 12px;width: 10%;"><b>Total<br>المجموع</b></th>
                    </tr>
                </thead>
                <tbody>
                    {% set total = namespace(amount = 0) %}
                    {%- for row in doc.items -%}
                        {% set total.amount = row.amount %}
                        <tr class="text-center">
                            <td>
                                <b>
                                    {{row.idx}}. {{ row.item_name or '' }}
                                    {% if(row.item_name_in_arabic) %}
                                        <br>{{ row.item_name_in_arabic or"" }}
                                    {% endif %}
                                </b> <br>
                                {% if(row.description) %}
                                    {{ row.description or '' }}
                                {% endif %}
                            </td>
                            <td>{{row.qty or ""}}<br>{{row.uom}}</td>
                            <td>{{ "{:,.2f}".format(row.rate or "") }}</td>
                            <td>{{ "{:,.2f}".format(row.amount)}}</td>
                            <td>
                                {% for tax in doc.taxes %}
                                    {% set data_object = json.loads(tax.item_wise_tax_detail) %}
                                    {% set key = row.item_code or row.item_name %}
                                    {%- if(data_object[key][0])-%}
                                        {{ frappe.format(data_object[key][0], {'fieldtype': 'Percent'}) }}
                                    {%- endif -%}
                                {% endfor %}
                            </td>
                            <td>
                                {% for tax in doc.taxes %}
                                    {% set data_object = json.loads(tax.item_wise_tax_detail) %}
                                    {% set key = row.item_code or row.item_name %}
                                    {% set tax_amount = (row.amount * tax.rate ) / 100 %}
                                    {%- if(data_object[key][1])-%}
                                        <span>{{ "{:,.2f}".format(tax_amount) }}</span>
                                        {% set total.amount = total.amount + tax_amount %}
                                    {%- endif -%}
                                {% endfor %}
                            </td>
                            <td>{{  frappe.format_value(frappe.utils.flt(total.amount, doc.precision('total_taxes_and_charges')), currency=doc.currency) }}</td>
                        </tr>
                    {%- endfor -%}
                    <tr style="height: 30px;">
                        <td colspan="7"></td>
                    </tr>
                    <tr>
                        <td colspan="5" class="text-right color_bg_gray bold" style="font-size: 12px;">Sub Total / المجموع الفرعي</td>
                        <td colspan="2" class="color_bg_gray bold" style="font-size: 12px;">{{ doc.get_formatted("total") }}</td>
                    </tr>
                    {%- if(doc.discount_amount) -%}
                    <tr>
                        <td colspan="5" class="text-right color_bg_gray bold" style="font-size: 12px;">Discount / تخفيض</td>
                        <td colspan="2" class="color_bg_gray bold" style="font-size: 12px;">{{ doc.get_formatted("discount_amount") }}</td>
                    </tr>
                    {%- endif -%}
                    <tr>
                        <td colspan="5" class="text-right color_bg_gray bold" style="font-size: 12px;">VAT@15% /  الضريبة المضافة %١٥</td>
                        <td colspan="2" class="color_bg_gray bold" style="font-size: 12px;">{{ doc.get_formatted("total_taxes_and_charges") }}</td>
                    </tr>
                    <tr>
                        <td colspan="5" class="text-right color_bg_gray bold" style="font-size: 12px;">Total / المجموع</td>
                        <td colspan="2" class="color_bg_gray bold" style="font-size: 12px;">{{ doc.get_formatted("grand_total") }}</td>
                    </tr>
                    <tr>
                        <td colspan="7" class="color_bg_gray text-center" style="font-size: 12px;"><b>Amount In Words:</b> {{ doc.in_words or""}}</td>
                    </tr>
                    {%- if(doc.terms) -%}
                        <tr>
                            <td colspan="4" style="font-size: 12px;"> {{doc.terms}}</td>
                            <td colspan="3" style="padding: 0px; text-align: right; vertical-align: middle; font-size: 15px;"
                           align="right">
                            
                            {% set inv_additional_fields_name = frappe.db.get_value('Sales Invoice Additional Fields', {"sales_invoice": doc.name}, "name") %}

                        {% if inv_additional_fields_name %}
                            {% set inv_additional_fields = frappe.get_doc("Sales Invoice Additional Fields", inv_additional_fields_name) %}
                        {% endif %}
                        
                        {% set ur = frappe.get_list('File', filters={ 'Attached_to_name': doc.name}, fields=['file_url']) %}
                        
                        <div class="qr-code-container">
                            {% if (inv_additional_fields and inv_additional_fields.qr_image_src) %}
                                <img class="qr-code" src="{{ inv_additional_fields.qr_image_src }}">
                            {% elif ur %}
                                {% for u in ur%}
                                    {% if u.file_url.endswith('.png') %}
                                        <img class="qr-code" src={{u.file_url}} style="padding-top: 10px;" />
                                    {% endif %}
                                    
                                {% endfor %}
                            {% else %}
                                <div class="qr-code" style="width:100px; height:100px;"></div>
                            {% endif %}

                        </div>

                           <!--<img class="qr-code" src={{doc.ksa_einv_qr}}>-->


                        </td>
                        </tr>
                    {%- endif -%}
                    

                </tbody>
            </table>
        </div>
    </div>
</div>

{% if print_settings.repeat_header_footer %}
    <div id="footer-html">
        {% if not no_letterhead and footer %}
            <div class="letter-head-footer">
                {{ footer }}
            </div>
        {% endif %}
    </div>
{% endif %}

